function wdShowNotification(){
	var total = 0;var notifications = document.querySelectorAll(".inbox .count, .notifications .count");
	for (var i = 0; i < notifications.length; i++){
		total += parseInt(notifications[i].innerHTML);
	}
	if(total > 0){
		WebDaemon.postNotification(0);
	}
}
wdShowNotification();
setInterval(wdShowNotification, 10000);

